class Todo{constructor(e){this.name=e,this.isCompleted=!1}}let todos=[];function addTodo(){let e=document.getElementById("toDoInput"),t=new Todo(e.value);""==e.value?errorMessage():(todos.push(t),e.value="",errorMsg.innerHTML="",render()),e.focus()}function render(){let e=document.getElementById("ul");e.innerHTML="";for(let t=0;t<todos.length;t++){let o=document.createElement("li");o.classList.add("list-group-item"),o.innerHTML=todos[t].name,e.appendChild(o);let d=document.createElement("input");d.classList.add("checkBox"),d.setAttribute("type","checkbox"),d.checked=todos[t].isCompleted,1==d.checked&&o.classList.add("itemIsChecked"),d.addEventListener("change",()=>{toggleCheckbox(todos[t].name),o.classList.toggle("itemIsChecked")}),o.appendChild(d);let n=document.createElement("button");n.classList.add("delete"),n.setAttribute("type","button"),n.setAttribute("aria-label","Delete button"),n.innerHTML='<i class="far fa-trash-alt"></i>',n.addEventListener("click",()=>{removeTodo(todos[t].name),o.remove()}),o.appendChild(n)}}function toggleCheckbox(e){let t=todoWithName(e);t.isCompleted=!t.isCompleted}function removeTodo(e){let t=document.getElementById("ul"),o=todoWithName(e),d=todos.indexOf(o);todos.splice(d,1),t.innerHTML="",render()}function todoWithName(e){for(let t=0;t<todos.length;t++)if(e==todos[t].name)return todos[t]}function errorMessage(){document.getElementById("errorMsg").innerHTML="Oops! You forgot to add a todo &#x1f644"}function sortTodo(){todos.sort(function(e,t){let o=e.name.toLowerCase(),d=t.name.toLowerCase();return o>d?1:d>o?-1:0}),render()}window.onload=function(){document.getElementById("addBtn").addEventListener("click",addTodo),document.getElementById("toDoInput").addEventListener("keyup",e=>{13!=e.key&&13!=e.keyCode||addTodo()}),document.getElementById("sortBtn").addEventListener("click",sortTodo);let e=new Todo("Clean the house"),t=new Todo("Wash the car");todos.push(e),todos.push(t),render()};